test-code.code:
import junit.framework.TestCase;
public class Foo extends TestCase {
    public void test1() {
        assertTrue(a==b);
    }
}

import junit.framework.TestCase;
public class Foo extends TestCase {
    public void test1() {
        Object a, b;
        assertTrue(a.equals(b));
    }
}

import junit.framework.TestCase;
public class Foo extends TestCase {
    public void test1() {
        SpecialEquals a, b;
        assertTrue(a.equals(b)); // not equals(Object)
    }

    class SpecialEquals {
        boolean equals(SpecialEquals b) {
            return false;
        }
    }
}

import static org.junit.Assert.*;
public class Foo {
    public void test1() {
        Object a, b;
        assertTrue(a.equals(b));
    }
}

import org.junit.Test;
import static org.junit.Assert.*;
public class Foo {
    @Test
    public void test1() {
        Object a, b;
        assertTrue(a.equals(b));
    }
}

import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.assertTrue;
public class Foo {
    @Test
    public void test1() {
        Object a, b;
        assertTrue(a.equals(b));
    }
}
